import './App.css';
import React from 'react';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      name: '这是父组件的值',
    }
  }

  onClick = (props) => {
    this.setState({
      name: props.name,
    })
  }
  render() {
    return (
      <div className="App">
        <div>{this.state.name}</div>
      </div>
    );
  }
}

class Greeting extends React.Component {
  constructor(props){
    super(props)
    this.state={
      name:'这是子组件的值',
      age:'这是子组件的岁数'
    }
  }
  toparent=()=>{
    this.props.onClick(this.state.name);
  }
  render() {
    return (
      <div>
        <input onChange={this.toparent}></input>
      </div>
    );
  }
}

