import './App.css';
import React from 'react';
import axios from 'axios'
import { Carousel } from 'antd';
import 'antd/dist/antd.css'

const contentStyle = {
  height: '30vh',
  color: '#fff',
  width: '50vw',
  lineHeight: '30vh',
  textAlign: 'center',
  background: '#364d79',
};

class Banner extends React.Component {
  render() {
    let list = this.props.article.map((item, index) => {
      return (
        <ul key={index}>
          <li className='list'>{item.name}</li>
        </ul>
      )
    })
    let img = this.props.article.map((item, index) => {
      return (
        <img key={index} src={item.img} style={{ height: '100%', width: '100%' }} alt='666'></img>
      )
    })
    return (
      <div className='banner clearFix'>
        {/* 这里是轮播图 */}
        <div className='banner-left clearFix'>
          <Carousel>
            <div>
              <h3 style={contentStyle}>{img[0]}</h3>
            </div>
            <div >
              <h3 style={contentStyle}>{img[1]}</h3>
            </div>
            <div>
              <h3 style={contentStyle}>{img[2]}</h3>
            </div>
            <div>
              <h3 style={contentStyle}>{img[3]}</h3>
            </div>
          </Carousel>
        </div>
        <div className='right-box'>
          {list}
        </div>
      </div>
    )
  }
}

class Content extends React.Component {
  render() {
    return (
      <div className='content'>

      </div>
    )
  }
}

export default class App extends React.Component {

  state = {
    article: [],
  }

  componentDidMount() {
    axios.get('http://rap2api.taobao.org/app/mock/273293/name').then((res => {
      this.setState({
        article: res.data.article
      })
      console.log(this.state.article[0].img)
    }))
  }

  render() {
    return (
      <div className='blog'>
        <Banner article={this.state.article} />
        <Content />
      </div>
    )
  }
}
