import './App.css';
import React from 'react';
import axios from 'axios'
import { Carousel } from 'antd';
import 'antd/dist/antd.css'

const contentStyle = {
  height: '30vh',
  color: '#fff',
  width: '50vw',
  lineHeight: '30vh',
  textAlign: 'center',
  background: '#364d79',
};


class Banner extends React.Component {
  render() {
    let list = this.props.article.map((item, index) => {
      return (
        <ul key={index}>
          <li className='list'>{item.detail.title}</li>
        </ul>
      )
    })

    let img = this.props.article.map((item, index) => {
      return (
        <img key={index} src={item.detail.img} style={{ height: '100%', width: '100%' }} alt='666'></img>
      )
    })

    return (
      <div className='banner clearFix'>
        <div className='banner-left clearFix'>
          <Carousel>
            <div>
              <h3 style={contentStyle}>{img[0]}</h3>
            </div>
            <div >
              <h3 style={contentStyle}>{img[1]}</h3>
            </div>
            <div>
              <h3 style={contentStyle}>{img[2]}</h3>
            </div>
            <div>
              <h3 style={contentStyle}>{img[3]}</h3>
            </div>
          </Carousel>
        </div>
        <div className='right-box'>
          {list}
        </div>
      </div>
    )
  }
}



class Content extends React.Component {
  render() {
    console.log('this.props.category', this.props.category);
    return (
      <div className='content'>
        <div className='category'>
          <h2>{this.props.category}</h2>
        </div>
        <div className='category-blog'>
          这里来个列表渲染
        </div>
      </div>
    )
  }
}



export default class App extends React.Component {

  state = {
    article: [],
    category: {},
  }

  componentDidMount() {
    axios.get('http://rap2api.taobao.org/app/mock/273293/name').then((res => {
      this.setState({
        article: res.data.article
      })
      console.log(this.state.article)
    }))

    axios.get('http://rap2api.taobao.org/app/mock/273293/category').then((res => {
      this.setState({
        category: res.data.category
      })
      console.log(this.state.category)
    }))
  }

  render() {
    return (
      <div className='blog'>
        <Banner article={this.state.article} />
        <Content category={this.state.category.a} />
        <Content category={this.state.category.b} />
        <Content category={this.state.category.c} />
        <Content category={this.state.category.d} />
      </div>
    )
  }
}
