import './App.css';
import React from 'react';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      name: '这是父组件的值',
    }
  }

  changeName=(name)=>{
    this.setState({
      name:name
    })
  }

  render() {
    return (
      <div className="App">
        <div>{this.state.name}</div>
        <button onClick>修改</button>
        <UserGreeting name={this.state.name}/>
        <Greeting changeName={this.changeName}/>
      </div>
    );
  }
}


class Greeting extends React.Component {
  constructor(props){
    super(props)
    this.state={
      name:'这是子组件的值',
    }
  }
  toparent=(e)=>{
    this.props.changeName(e.target.value);
  }
  render() {
    return (
      <div>
        <input onChange={this.toparent}></input>
      </div>
    );
  }
}


class UserGreeting extends React.Component {
  state={
    name:'这是我的值'
  }
  render() {
    return <h1>{this.state.name}{this.props.name}</h1>;
  }
}

