import './App.css';
import React from 'react';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      name: '这是父组件的值',
      age: 16
    }
  }

  onClick = (a, e) => {
    this.setState({
      name: a,
      age: e.target.value
    })
  }
  render() {
    return (
      <div className="App">
        <div>name:{this.state.name}</div>
        <div>age:{this.props.age}</div>
        <button onClick={onClick} value='6666'>修改</button>
        <Greeting name='6666'/>
      </div>
    );
  }
}

class Greeting extends React.Component {
  constructor(props){
    super(props)
    this.state={
      name:'这是子组件的值'
    }
  }
  toparent=()=>{
    const {onClick} = this.props;
    onClick(this.state.name);
  }
  render() {
    return (
      <div>
        <h1>{this.state.name}</h1>
        <button onClick={this.toparent} value='888888'>再改</button>
      </div>
    );
  }
}

