import './App.css';
import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      name: '这是父组件的值',
      login: 0
    }
  }

  change = () => {
    if (this.state.login === 1) {
      this.setState({
        login : 0
      })
    } else {
      this.setState({
        login : 1
      })
    }
  }

  changeName = (name) => {
    this.setState({
      name: name
    })
  }

  render() {
    let button;
    if (this.state.login === 1) {
      button = <button onClick={this.change}>太他妈帅了</button>
    } else {
      button = <button onClick={this.change}>最帅</button>
    }
    return (
      <div className="App">
        <div>{this.state.name}</div>
        {button}
        <button onClick={(e)=>{this.changeName('')}}>改名</button>
        <UserGreeting name={this.state.name} />
        <Greeting changeName={this.changeName} />
      </div>
    );
  }
}


class Greeting extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      name: '这是子组件的值',
    }
  }
  toparent = (e) => {
    this.props.changeName(e.target.value);
  }
  render() {
    return (
      <div>
        <input onChange={this.toparent}></input>
      </div>
    );
  }
}


class UserGreeting extends React.Component {
  state = {
    name: '这是我的值'
  }
  render() {
    return <h1>{this.state.name}{this.props.name}</h1>;
  }
}

