import './App.css';
import React from 'react';
import axios from 'axios'

class App extends React.Component {

  state = {
    name: [],
    login: 0
  }

  componentDidMount(){
    axios.get('http://rap2api.taobao.org/app/mock/273293/name').then((res)=>{
      console.log(res.data.name);
      this.setState({
        name:res.data.name,
      })
    })
  }

  render() {
    let button;
    if (this.state.login === 1) {
      button = <button onClick={this.change}>太他妈帅了</button>
    } else {
      button = <button onClick={this.change}>最帅</button>
    }
    return (
      <div className="App">
        <div>{this.state.name}</div>
        {button}
        <button onClick={() => { this.sort() }}>分组</button>
        <UserGreeting name={this.state.name} />
        <Greeting changeName={this.changeName} />
      </div>
    );
  }

  sort=()=>{
    this.setState({
      name:this.setState.name.split('')
    })
    console.log(this.s)
  }

  change = () => {
    if (this.state.login === 1) {
      this.setState({
        login: 0
      })
    } else {
      this.setState({
        login: 1
      })
    }
  }

  changeName = (name) => {
    this.setState({
      name: name
    })
  }
}


class Greeting extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      name: '这是子组件的值',
    }
  }
  toparent = (e) => {
    this.props.changeName(e.target.value);
  }
  render() {
    console.log(this.props)
    return (
      <div>
        <input onChange={this.toparent}></input>
      </div>
    );
  }
}


class UserGreeting extends React.Component {
  state = {
    name: '这是我的值'
  }
  render() {
    return <h1>{this.state.name}{this.props.name}</h1>;
  }
}

