import React from 'react';
import axios from 'axios';
import '../style/list.css';
import { Pagination } from 'antd';
import 'antd/dist/antd.css';
import {Link} from 'react-router-dom'

export default class List extends React.Component {

    state = {
        article: [],
        current: 1,
        minValue: 0,
        maxValue: 7
    }

    componentDidMount() {
        axios.get('http://rap2api.taobao.org/app/mock/273293/allArticle').then((res) => {
            this.setState({
                article: res.data.article
            })
            console.log(this.state.article)
        })
    }

    render() {
        
        let list = this.state.article.slice(this.state.minValue, this.state.maxValue).map((item, index) => {
            let detail = {pathname:'/detail',state:{title:item.title,img:item.img}}
            return (
                <div key={index} className='list-blog'>
                    <img alt='666' src={item.img}></img>
                    <Link to={detail}><p onClick={()=>{this.detail(item.title)}}>{item.title}</p></Link>
                </div>
            )
        })
        return (
            <div className='wrapper'>
                {list}
                <footer>
                    <Pagination onChange={this.changePage}
                        total={this.state.article.length}
                        current={this.state.current}
                        showQuickJumper={true} />
                </footer>
            </div>

        )
    }

    detail=(e)=>{
        console.log(e)
    }

    changePage = (page) => {
        if (page <= 1) {
            this.setState({
                minValue: 0,
                maxValue: 7,
                current:page
            });
        } else {
            this.setState({
                minValue: (page - 1) * 7,
                maxValue: (page - 1) * 7 + 7,
                current:page
            });
        }
    }
}
