import './App.css';
import React from 'react';
import axios from 'axios'
import { Carousel } from 'antd';
import 'antd/dist/antd.css'

const contentStyle = {
  height: '30vh',
  color: '#fff',
  lineHeight: '30vh',
  textAlign: 'center',
  background: '#364d79',
};

class Banner extends React.Component {
  render() {
    if(this.props.article){console.log('这是第一张图',this.props.article[0].img)}
    // console.log('这是第一张图',this.props.article[0].img)
    let list = this.props.article.map((item, index) => {
      return (
        <ul key={index}>
          <li className='list'>{item.name}</li>
        </ul>
      )
    })
    return (
      <div className='banner clearFix'>
        {/* 这里是轮播图 */}
        <div className='banner-left clearFix'>
          <Carousel>
            <div>
              <h3 style={contentStyle}>{this.props.article.img}</h3>
            </div>
            <div>
              <h3 style={contentStyle}>2</h3>
            </div>
            <div>
              <h3 style={contentStyle}>3</h3>
            </div>
            <div>
              <h3 style={contentStyle}>4</h3>
            </div>
          </Carousel>
        </div>
        <div className='right-box'>
          {list}
        </div>
      </div>
    )
  }
}

export default class App extends React.Component {

  state = {
    article: [],
  }

  componentDidMount() {
    axios.get('http://rap2api.taobao.org/app/mock/273293/name').then((res => {
      this.setState({
        article: res.data.article
      })
      console.log(res.data.article[0].img)
    }))
  }

  render() {
    return (
      <div className='blog'>
        <Banner article={this.state.article} />
      </div>
    )
  }
}
